languagesToTest = ['English','Spanish', 'French', 'Portuguese', 'German', 'Turkish', 'Italian', 'Polish', 'Slovak', 'Croatian', 'Czech']


divider = '-' * 80 + '\n'

def missingFrom(fontGlyphs, languageGlyphs):
    missing = []
    for glyph in languageGlyphs:
        if glyph not in fontGlyphs:
            missing.append(glyph)
    return(missing)

print(divider)

fontName = f.info.familyName + ' ' + f.info.styleName

print(fontName, end = '\n')
print('-' * len(fontName), end = '\n\n')

for language in languagesToTest:
    languageGlyphs = languageDict[language]
    missing = missingFrom(currentFontGlyphs, languageGlyphs)
    
    print(str(language)+': ', end='')
    if missing == []:
        print('---',end='\n\n')
    else:
        print(*missing,sep=', ', end='\n\n')

